mapper.update:
update orders
cancel_reason=#{cancelReason}, rejection_reason=#{rejectionReason}, cancel_time=#{cancelTime}, pay_status=#{payStatus}, pay_method=#{payMethod}, checkout_time=#{checkoutTime}, status = #{status}, delivery_time = #{deliveryTime}
where id = #{id}

update orders
set status = #{cancelled}, cancel_reason = #{cancelReason}, cancel_time = #{cancelTime}
where id in

    #{id}

and status = #{accessWord}

update orders
set status = #{completed}, delivery_time = #{deliveryTime}
where id in

    #{id}

and status = #{accessWord}

mapper.select:
select * from orders


        and status = #{status}


        and user_id = #{userId}


        and phone like concat('%',#{phone},'%')


        and number like concat('%',#{number},'%')


        and order_time between #{beginTime} and #{endTime}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}
